import './App.css';
import React from 'react';
import { useRoutes } from 'react-router-dom';
import carImg from './car.webp';
import Home from './pages/Home';
import ReadPosts from './pages/ReadPosts';
import CreatePost from './pages/CreatePost';
import EditPost from './pages/EditPost';
import { Link } from 'react-router-dom';

const App = () => {
  const descr =
    'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.';

  const posts = [
    {
      id: '1',
      title: 'Cartwheel in Chelsea ü§∏üèΩ‚Äç‚ôÄÔ∏è',
      author: 'Harvey Milian',
      description: descr,
    },
    {
      id: '2',
      title: 'Love Lock in Paris üîí',
      author: 'Beauford Delaney',
      description: descr,
    },
    {
      id: '3',
      title: 'Wear Pink on Fridays üéÄ',
      author: 'Onika Tonya',
      description: descr,
    },
    {
      id: '4',
      title: 'Adopt a Dog üê∂',
      author: 'Denise Michelle',
      description: descr,
    },
  ];

  // Sets up routes
  let element = useRoutes([
    {
      path: '/',
      element: <Home data={posts} />,
    },
    {
      path: '/read',
      element: <ReadPosts />,
    },
    {
      path: '/edit/:id',
      element: <EditPost data={posts} />,
    },
    {
      path: '/new',
      element: <CreatePost />,
    },
  ]);

  return (
    <div className="App">
      <div className="navbar">
        <Link to="/">
          <button className="headerBtn"> Explore Characters üîç </button>
        </Link>
        <Link to="/new">
          <button className="headerBtn">Create a Character</button>
        </Link>
      </div>

      <div className="header">
        <h1>üëæ Create a Character üëæ</h1>
        <p>
          Check the side navigation bar to create characters, edit yours, or
          browse a crew gallery
        </p>
        <img className="main_img" alt="edit button" src={carImg} />

        {element}
      </div>
    </div>
  );
};

export default App;
