    const [posts, setPosts] = useState([]);

    useEffect(() => {
        const fetchPost = async () => {
            const {data} = await supabase
                .from('posts')
                .select()
                .order('created_at', {ascending: true})
            console.log(data);
            setPosts(data)
        }
        fetchPost();
    }, []);
    console.log("Posts being rendered:", posts);
    return (
        <div className="ReadPosts">
            {
                posts && posts.length > 0 ?
